import { useWorkoutsContext } from '../hooks/useWorkoutsContext'

import formatDistanceToNow from 'date-fns/formatDistanceToNow'

const WorkoutDetails = ({ workout }) => {

    const {dispatch} = useWorkoutsContext()

    const handleClick = async () => {
        const response = await fetch('/api/workouts/' + workout._id, {
            method: 'DELETE'
        })

        const json = await response.json()

        if(response.ok) {
            dispatch({type:'DELETE_WORKOUT', payload: json})
        }
    }

    const handleUpdateClick = async () => {
        const response = await fetch('/api/workouts/' + workout._id, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ load, reps })
        })

        const updatedWorkout = await response.json()

        if(response.ok) {
            dispatch({type:'UPDATE_WORKOUT', payload: updatedWorkout})
            setEditMode(false)
        }
    }
    return (
        <div className="workout-details">
            { editMode ? (
                <>
                    <input type='number' placeholder='Load' value={load} onChange={(e) => setLoad(e.target.value)} />
                    <input type='number' placeholder='Reps' value={reps} onChange={(e) => setReps(e.target.value)} />
                    <button className='material-symbols-outlined' onClick={handleUpdateClick}>Save</button>
                </>
            ) : (
                <>
                    <h4>{workout.title}</h4>
                    <p><strong>Load (kg):</strong>{workout.load}</p>
                    <p><strong>Reps:</strong>{workout.reps}</p>
                    <p>{formatDistanceToNow(new Date(workout.createdAt), { addSuffix: true })}</p>
                    <span className='material-symbols-outlined delete' onClick={handleClick}>Delete</span>
                    <span className='material-symbols-outlined edit' onClick={() => setEditMode(true)}>Edit</span>
                </>
            )}
        </div>
    )
}

export default WorkoutDetails
