import { useState } from "react";
import { useWorkoutsContext } from "../hooks/useWorkoutsContext";

const WorkoutEdit = ({ workout, onClose }) => {
  const { dispatch } = useWorkoutsContext();
  const [title, setTitle] = useState(workout.title);
  const [load, setLoad] = useState(workout.load);
  const [reps, setReps] = useState(workout.reps);

  const handleUpdate = async () => {
    const response = await fetch('/api/workouts/' + workout._id, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ title, load, reps })
    })

    const json = await response.json()

    if(response.ok) {
      dispatch({type:'UPDATE_WORKOUT', payload: json})
      onClose()
    }
  }

  return (
    <div className="workout-edit">
      <h4>Edit Workout</h4>
      <form onSubmit={(e) => { e.preventDefault(); handleUpdate(); }}>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input type="text" id="title" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </div>
        <div className="form-group">
          <label htmlFor="load">Load (kg)</label>
          <input type="number" id="load" value={load} onChange={(e) => setLoad(e.target.value)} required />
        </div>
        <div className="form-group">
          <label htmlFor="reps">Reps</label>
          <input type="number" id="reps" value={reps} onChange={(e) => setReps(e.target.value)} required />
        </div>
        <div className="form-group">
          <button type="submit">Update</button>
          <button type="button" onClick={onClose}>Cancel</button>
        </div>
      </form>
    </div>
  )
}

export default WorkoutEdit;
